<?php	if (isset($_POST['name'])) {		if (preg_match("/[a-zA-Zа-яА-Я]/", $_POST['name'])) {			$name = $_POST['name'];		} else { $name = ''; }	} else { $name = ''; }		if (isset($_POST['submit'])) {		if ($name) {			$character_db = @mysql_query("SELECT name, race, guid, gender, class, level, account FROM `".$mysql_db_characters[$realm_num]."`.`characters` WHERE name LIKE '%$name%' LIMIT 100", $ConnectDB[$realm_num]);			if (@mysql_num_rows($character_db)) {				$armory_search_page = 					"					<table cellspacing=\"0\" class=\"list_table\">						<thead>								<tr height=\"33\">									<th width=\"300\">Имя персонажа</th>									<th width=\"100\">Раса</th>									<th width=\"100\">Класс</th>									<th width=\"100\">Фракция</th>									<th width=\"100\">Уровень</th>									<th width=\"300\">Гильдия</th>								</tr>							</thead>					";					$i = "1";				while($result = mysql_fetch_array($character_db)){					$guidlid = @mysql_query("SELECT guildid FROM ".$mysql_db_characters[$realm_num].".guild_member WHERE guid = '".$result['guid']."'", $ConnectDB[$realm_num]);					if (@mysql_num_rows($guidlid)) {						$guidlid = @mysql_fetch_array($guidlid);						$guidlname = @mysql_fetch_array(mysql_query("SELECT name FROM ".$mysql_db_characters[$realm_num].".guild WHERE guildid = '".$guidlid['guildid']."'", $ConnectDB[$realm_num]));						$guidlname = "\"".$guidlname['name']."\"";					} else { $guidlname = "Не состоит";}					if ($result['race'] == 1 || $result['race'] == 3 || $result['race'] == 4 || $result['race'] == 7 || $result['race'] == 11) { $char_side = "0"; } else { $char_side = "1";  };					$armory_search_page.=						"							<tr>								<td class=\"col$i\" align=\"center\"><a href=\"./?page=armory&realm=$realm_num&do=viewchar&guid=".$result['guid']."\">".$result['name']."</a></td>								<td class=\"col$i\" align=\"center\"><img src=\"./style/images/icon/".$result['race']."-".$result['gender'].".gif\" height=\"18\" width=\"18\" onmouseover=\"Tip('".$race_array[$result['gender']][$result['race']]."', WIDTH, 350, OFFSETX, 10, OFFSETY, -40, STICKY, false);\"></td>								<td class=\"col$i\" align=\"center\"><img src=\"./style/images/icon/".$result['class'].".gif\" height=\"18\" width=\"18\" onmouseover=\"Tip('".$class_array[$result['gender']][$result['class']]."', WIDTH, 350, OFFSETX, 10, OFFSETY, -40, STICKY, false);\"></td>								<td class=\"col$i\" align=\"center\"><img src=\"./style/images/icon/side-$char_side.png\" height=\"18\" width=\"18\"></td>								<td class=\"col$i\" align=\"center\">".$result['level']."</td>								<td class=\"col$i\" align=\"center\">$guidlname</td>							</tr>						";					if ($i=="1") {$i="2";} else {$i="1";}				}				$armory_search_page .= "</table><br>";			} else {				$armory_search_page = 					"						<br>						<center>По вашему запросу ничего не найдено. Вы будите автоматически возвращены через 5 секунд</center><script type=\"text/javascript\">setTimeout('location.replace(\"./?page=armory&realm=$realm_num&do=search\")', 5000);</script>						<br>					";			}		} else {			$armory_search_page = 				"					<br>					<center>Ваш запрос не корректный. Вы будите автоматически возвращены через 5 секунд</center><script type=\"text/javascript\">setTimeout('location.replace(\"./?page=armory&realm=$realm_num&do=search\")', 5000);</script>					<br>				";		}	} else {		$armory_search_page =			"			<center>				<form action=\"?page=armory&realm=$realm_num&do=search\" method=\"post\" accept-charset=\"cp1251\">					<br>Ник:<br> <input type=\"text\" class=\"input_textbox\" name=\"name\"><br>					<input type=\"submit\" class=\"input_button\" value=\"Найти\" name=\"submit\">				</form>			</center>			";	}	?><div class="mb_top">Поиск персонажа на реалме <?php echo $realm_title[$realm_num];?></div><div class="mb_main">	<?php echo $armory_search_page;?></div><div class="mb_down"></div>